    private void tKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_tKeyReleased
        javax.swing.table.DefaultTableModel dtm = 
                        (javax.swing.table.DefaultTableModel)table.getModel();
        int rc = dtm.getRowCount();
        while(rc-- != 0){
            dtm.removeRow(0);
        }
        String n = t.getText();
        try{
            ResultSet rs = db.DbConnect.st.executeQuery("select * from product where name like '%"+n+"%'");
            while(rs.next()){
                int pid = rs.getInt("pid");
                String name = rs.getString("name");
                int cp = rs.getInt("costprice");
                int sp = rs.getInt("sellingprice");
                int qty = rs.getInt("quantity");
                String info = rs.getString("information");
                
                java.util.Vector r = new java.util.Vector();
                r.add(pid);
                r.add(name);
                r.add(cp);
                r.add(sp);
                r.add(qty);
                r.add(info);
                
                
                dtm.addRow(r);
            }
        }catch(Exception ex){
            JOptionPane.showMessageDialog(null,ex);
        }
    }//GEN-LAST:event_tKeyReleased

