    private void tableMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tableMouseClicked
         int ri = table.getSelectedRow();
         
         int id = (int)table.getValueAt(ri,0);
         String name = (String)table.getValueAt(ri,1);
         int costprice = (int)table.getValueAt(ri,2);
         int sellingprice = (int)table.getValueAt(ri,3);
         int quantity = (int)table.getValueAt(ri,4);
         String information = (String)table.getValueAt(ri,5);
         
         pid.setText(id+"");
         n.setText(name);
         cp.setText(costprice+"");
         sp.setText(sellingprice+"");
         qty.setText(quantity+"");
         info.setText(information);
    }//GEN-LAST:event_tableMouseClicked

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // Update
            int id = Integer.parseInt(pid.getText());
            String name = n.getText();
            int costPrice = Integer.parseInt(cp.getText());
            int sellingPrice = Integer.parseInt(sp.getText());
            int quantity = Integer.parseInt(qty.getText());
            String information = info.getText();
            
            if(!name.equalsIgnoreCase("")){
            try{   
  db.DbConnect.st.executeUpdate("update product set name='"+name+"',costprice="+costPrice+",sellingprice="+sellingPrice+",quantity="+quantity+",information='"+information+"' where pid="+id);
                     
            JOptionPane.showMessageDialog(null,"Product Updated Successfully!");
            tKeyReleased(null);
        }catch(Exception ex){
            JOptionPane.showMessageDialog(null,ex);
        }
            }else{
                JOptionPane.showMessageDialog(null,"Plz Fill The complete information");
            }      
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // Delete
        int r = JOptionPane.showConfirmDialog(null,"Do you Really Wanna Delete ??",
                "Deletion Confirmation",JOptionPane.YES_NO_OPTION);
        if(r == JOptionPane.YES_OPTION){
            try{   
             int id = Integer.parseInt(pid.getText());
  db.DbConnect.st.executeUpdate("delete from product where pid="+id);
                     
            JOptionPane.showMessageDialog(null,"Product Deleted Successfully!");
            tKeyReleased(null);
        }catch(Exception ex){
            JOptionPane.showMessageDialog(null,ex);
        }
        }
        
    }//GEN-LAST:event_jButton2ActionPerformed

